import React from "react"

class MemeGenerator extends React.Component{
    constructor(){
        super()
        this.state={
            topText:"",
            bottomText:"",
            randomImg:"https://imgflip.com/s/meme/Bernie-I-Am-Once-Again-Asking-For-Your-Support.jpg",
            allMemeImmage : []
        }
    }
    componentDidMount(){
        fetch("https://api.imgflip.com/get_memes")
            .then(response => response.json())
            .then(response => {
                const{memes} = response.data
                console.log(memes[0])
                this.setState({ allMemeImmage : memes})
            })
    }
    render(){
        return(
            
            <h1>Meme</h1>
        )
    }
}
export default MemeGenerator;
