import React, { Component } from "react";
import Conditional from "./Conditional"
class MyApp extends React.Component{
      constructor()
      {
            super();
            this.state={
                  isLoading:true
            }
            
      }
      
      componentDidMount(){
            setTimeout(() => {
                  this.setState({
                        isLoading: false
                  })
            },2000)
      }
      render()
      {
            return(
                  <div>
                        <conditional isLoading={this.state.isLoading}/>
                  </div>
            )
      }
     
}
export default MyApp;
